// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

interface INonFungiblePlayer is IERC721 {
    struct NfpInfo {
        uint256 gen;
        uint256 level;
        uint256 skillPoint;
        string position;
        string rarity;
        string class;
    }

    function updateTraitsNFP(
        uint256 _tokenId,
        uint256 _gen,
        uint256 _level,
        uint256 _skillPoint,
        string memory _position,
        string memory _rarity,
        string memory _class
    ) external;

    function mintFusionedNFP(
        uint256 _tokenId1,
        uint256 _tokenId2,
        string memory _position,
        string memory _rarity,
        string memory _class
    ) external;

    function getSkillPoint(uint256 _tokenId) external view returns (uint256);

    function getGeneration(uint256 _tokenId) external view returns (uint256);

    function getLevel(uint256 _tokenId) external view returns (uint256);

    function getPosition(uint256 _tokenId)
        external
        view
        returns (string memory);

    function getRarity(uint256 _tokenId) external view returns (string memory);

    function getClass(uint256 _tokenId) external view returns (string memory);

    function setSkillPoint(uint256 _tokenId, uint256 _skillPoint) external;

    function setGeneration(uint256 _tokenId, uint256 _gen) external;

    function setLevel(uint256 _tokenId, uint256 _level) external;

    function setPosition(uint256 _tokenId, string memory _position) external;

    function setRarity(uint256 _tokenId, string memory _rarity) external;

    function setClass(uint256 _tokenId, string memory _class) external;

    function burn(uint256 _tokenId) external;
}
